function showInfo(){info.classList.add("show")}function hideInfo(){idleTime=0,info.classList.remove("show")}function timerIncrement(){console.log(idleTime),idleTime++,idleTime>5&&hideInfo()}var info=document.getElementById("info"),idleTime=0;window.addEventListener("mousemove",function(e){idleTime=0,showInfo()}),window.addEventListener("keypress",function(e){idleTime=0}),function(){setInterval(timerIncrement,1e3)}();