function showInfo(){idleTime=0,infoPopUp.classList.add("show"),infoWindow.classList.add("show")}function hideInfo(){infoPopUp.classList.remove("show"),infoWindow.classList.remove("show")}function timerIncrement(){idleTime++,idleTime>2&&hideInfo()}var infoPopUp=document.querySelector(".info--popup"),infoWindow=document.querySelector(".info--window"),idleTime=0;window.addEventListener("mousemove",function(o){showInfo()}),window.addEventListener("keydown",function(o){hideInfo()}),function(){setInterval(timerIncrement,1e3)}();