var clips={midi:document.querySelectorAll('.clip-box[data-type="midi"] .clip-box'),phone:document.querySelectorAll('.clip-box[data-type="phone"] .clip-box'),keyboard:document.querySelectorAll('.clip-box[data-type="keyboard"] .clip-box')},getChar=function(e){return console.log(String.fromCharCode(e.keyCode).toLowerCase()),String.fromCharCode(e.keyCode).toLowerCase()},getClipBox=function(e){return document.querySelector(".clip-box--clip[data-name="+getChar(e)+"]")};window.addEventListener("keydown",function(e,o){var t=getClipBox(e);t&&t.classList.add("active")}),window.addEventListener("keyup",function(e,o){var t=getClipBox(e);t&&t.classList.remove("active")});